def get_walls(layout) :
    layout_height = len(layout)
    layout_width = len(layout[0])
    
    wall_cordenates = []
    for y in range (0, layout_height) :
        for x in range (0, layout_width) :
            if layout[y][x] == "⬜" :
                wall_cordenates.append([y, x])
    
    return wall_cordenates

wall_cordenates = get_walls(layout)

